tables.table_schema:
CREATE TABLE fintech_firms (
  firm_id INT PRIMARY KEY,
  firm_name VARCHAR(50),
  data_retention_policy VARCHAR(100)
);

CREATE TABLE regulatory_guidelines (
  guideline_id INT PRIMARY KEY,
  guideline_name VARCHAR(50),
  description TEXT,
  effective_date DATE
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100),
    created_at TIMESTAMP
);

CREATE TABLE transactions (
    transaction_id INT PRIMARY KEY,
    user_id INT,
    amount DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(100),
    description TEXT,
    effective_date DATE
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    address VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(100),
    description TEXT
);
