CREATE TABLE borrowers (
  borrower_id UUID PRIMARY KEY,
  borrower_name VARCHAR(50),
  loan_amount DECIMAL(10, 2),
  loan_purpose TEXT
);

CREATE TABLE loans (
  loan_id UUID PRIMARY KEY,
  loan_type VARCHAR(50),
  interest_rate DECIMAL(5, 2),
  loan_term INT
);

CREATE TABLE lenders (
  lender_id UUID PRIMARY KEY,
  lender_name VARCHAR(50),
  investment_amount DECIMAL(10, 2),
  investment_status BOOLEAN
);

CREATE TABLE loan_opportunities (
  opportunity_id UUID PRIMARY KEY,
  loan_id UUID REFERENCES loans(loan_id),
  borrower_id UUID REFERENCES borrowers(borrower_id),
  opportunity_status BOOLEAN
);

CREATE TABLE investments (
  investment_id UUID PRIMARY KEY,
  lender_id UUID REFERENCES lenders(lender_id),
  opportunity_id UUID REFERENCES loan_opportunities(opportunity_id),
  investment_amount DECIMAL(10, 2)
);
