tables.table_schema:
CREATE TABLE credit_scores (
    id INT PRIMARY KEY,
    borrower_name VARCHAR(255),
    credit_score INT,
    last_updated DATE
);

CREATE TABLE borrower_credit_scores (
    borrower_id INT PRIMARY KEY,
    credit_score INT
);

CREATE TABLE credit_alerts (
    alert_id INT PRIMARY KEY,
    borrower_id INT,
    alert_message TEXT,
    alert_date TIMESTAMP
);

CREATE TABLE Borrowers (
  borrower_id INT PRIMARY KEY,
  borrower_name VARCHAR(255),
  credit_score INT
);

CREATE TABLE CreditScores (
  credit_score_id INT PRIMARY KEY,
  borrower_id INT,
  credit_score INT,
  check_date DATE
);

CREATE TABLE CreditRiskAlerts (
  alert_id INT PRIMARY KEY,
  borrower_id INT,
  credit_risk_message TEXT,
  alert_date DATETIME
);

CREATE TABLE Borrowers (
    borrower_id UUID PRIMARY KEY,
    borrower_name VARCHAR(50),
    credit_score INT
);

CREATE TABLE Loans (
    loan_id UUID PRIMARY KEY,
    borrower_id UUID REFERENCES Borrowers(borrower_id),
    loan_amount DECIMAL(10, 2),
    loan_status VARCHAR(20)
);

CREATE TABLE CreditAlerts (
    alert_id UUID PRIMARY KEY,
    borrower_id UUID REFERENCES Borrowers(borrower_id),
    alert_message TEXT,
    alert_date TIMESTAMP
);

CREATE TABLE CreditHistory (
    history_id UUID PRIMARY KEY,
    borrower_id UUID REFERENCES Borrowers(borrower_id),
    credit_change INT,
    history_date TIMESTAMP
);

CREATE TABLE CreditScores (
    score_id UUID PRIMARY KEY,
    borrower_id UUID REFERENCES Borrowers(borrower_id),
    credit_score INT,
    score_date TIMESTAMP
);
