CREATE TABLE repayment_schedule (
  schedule_id UUID PRIMARY KEY,
  borrower_id UUID REFERENCES borrowers(borrower_id),
  payment_date DATE NOT NULL,
  payment_amount DECIMAL(15, 2) NOT NULL
);
