tables.table_schema:
CREATE TABLE user_behavior_tracking (
    user_id UUID PRIMARY KEY,
    action_timestamp TIMESTAMP,
    action_type VARCHAR,
    page_visited VARCHAR
);

CREATE TABLE UserBehavior (
    user_id INT PRIMARY KEY,
    behavior_type VARCHAR(255),
    behavior_timestamp TIMESTAMP
);

CREATE TABLE PersonalizedOffers (
    offer_id INT PRIMARY KEY,
    user_id INT FOREIGN KEY REFERENCES UserBehavior(user_id),
    offer_description TEXT,
    offer_expiry_date DATE
);

CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    notification_preference VARCHAR(20),
    language_preference VARCHAR(20),
    theme_preference VARCHAR(20)
);

CREATE TABLE user_profiles (
  user_id INT PRIMARY KEY,
  name VARCHAR(50),
  age INT,
  gender VARCHAR(10)
);

CREATE TABLE user_preferences (
  user_id INT PRIMARY KEY,
  language VARCHAR(20),
  notification_preference VARCHAR(20),
  theme_color VARCHAR(20)
);

CREATE TABLE behavior_analysis (
  analysis_id INT PRIMARY KEY,
  user_id INT,
  pattern VARCHAR(100),
  result VARCHAR(50)
);

CREATE TABLE offer_history (
  offer_id INT PRIMARY KEY,
  user_id INT,
  offer_details VARCHAR(100),
  expiration_date TIMESTAMP
);
