tables.table_schema:
CREATE TABLE historical_stock_prices (
    stock_symbol VARCHAR(10),
    date DATE,
    price DECIMAL(10, 2)
);

CREATE TABLE market_data (
    market_index VARCHAR(10),
    date DATE,
    sentiment_score DECIMAL(5, 2),
    trading_volume INT
);

CREATE TABLE market_data (
    date DATE,
    market_index STRING,
    market_value DECIMAL
);

CREATE TABLE ml_models (
    model_id INT,
    model_name STRING,
    training_data_table STRING,
    algorithm_used STRING
);
