tables.table_schema:
CREATE TABLE economic_indicators (
  id INT PRIMARY KEY,
  indicator_name VARCHAR,
  indicator_value FLOAT
);

CREATE TABLE financial_institutions (
  id INT PRIMARY KEY,
  institution_name VARCHAR,
  assets FLOAT,
  liabilities FLOAT
);

CREATE TABLE economic_indicators (
  id INT PRIMARY KEY,
  indicator_name VARCHAR(50),
  indicator_value DECIMAL(10,2),
  data_source VARCHAR(100)
);

CREATE TABLE financial_institutions (
  id INT PRIMARY KEY,
  institution_name VARCHAR(100),
  industry_sector VARCHAR(50),
  location VARCHAR(100)
);

CREATE TABLE economic_indicators (
  indicator_id INT PRIMARY KEY,
  indicator_name TEXT,
  value DECIMAL
);

CREATE TABLE investments (
  investment_id INT PRIMARY KEY,
  investment_name TEXT,
  amount DECIMAL,
  date DATE
);

CREATE TABLE investors (
  investor_id INT PRIMARY KEY,
  investor_name TEXT,
  location TEXT
);

CREATE TABLE economic_indicators (
  indicator_id INT PRIMARY KEY,
  indicator_name VARCHAR(100) NOT NULL,
  indicator_category VARCHAR(50),
  indicator_value DECIMAL(10, 2) NOT NULL
);

CREATE TABLE investment_firms (
  firm_id INT PRIMARY KEY,
  firm_name VARCHAR(100) NOT NULL,
  founding_date DATE,
  headquarters_location VARCHAR(100)
);

CREATE TABLE client_portfolios (
  portfolio_id INT PRIMARY KEY,
  client_id INT,
  portfolio_value DECIMAL(15, 2) NOT NULL,
  FOREIGN KEY (client_id) REFERENCES clients(client_id)
);

CREATE TABLE clients (
  client_id INT PRIMARY KEY,
  client_name VARCHAR(100) NOT NULL,
  client_type VARCHAR(50),
  total_assets DECIMAL(15, 2)
);
