tables.table_schema:
CREATE TABLE portfolio_management (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    investment_amount DECIMAL
);

CREATE TABLE trading_patterns (
    pattern_id INT PRIMARY KEY,
    user_id INT,
    pattern_details TEXT,
    FOREIGN KEY (user_id) REFERENCES portfolio_management(user_id)
);

CREATE TABLE trades (
    trade_id INT PRIMARY KEY,
    trade_timestamp TIMESTAMP,
    trade_symbol VARCHAR(50),
    trade_volume INT
);

CREATE TABLE investor_sentiment (
    sentiment_id INT PRIMARY KEY,
    investor_id INT,
    sentiment_score FLOAT,
    sentiment_date DATE
);

CREATE TABLE portfolio_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(50),
    strategy_description TEXT
);

CREATE TABLE TradingPatterns (
    id INT PRIMARY KEY,
    pattern_name VARCHAR(50) NOT NULL,
    pattern_details TEXT
);

CREATE TABLE InvestorSentiment (
    id INT PRIMARY KEY,
    investor_id INT,
    sentiment VARCHAR(50) NOT NULL,
    sentiment_date DATE
);

CREATE TABLE PortfolioManagement (
    id INT PRIMARY KEY,
    portfolio_name VARCHAR(50) NOT NULL,
    strategy_details TEXT
);

CREATE TABLE AIAnalysis (
    id INT PRIMARY KEY,
    analysis_type VARCHAR(50) NOT NULL,
    analysis_results JSON
);

CREATE TABLE portfolio_management ( 
   id SERIAL PRIMARY KEY, 
   portfolio_name VARCHAR(255) NOT NULL, 
   analysis_result JSONB, 
   optimization_strategy VARCHAR(255) 
);

CREATE TABLE performance_metrics ( 
   id SERIAL PRIMARY KEY, 
   metric_name VARCHAR(255) NOT NULL, 
   metric_value FLOAT, 
   metric_description TEXT 
);
