tables.table_schema:
CREATE TABLE payment_gateways (
    gateway_id INT PRIMARY KEY,
    gateway_name VARCHAR(255),
    gateway_type VARCHAR(50),
    api_key VARCHAR(100)
);

CREATE TABLE payment_gateways (
    id UUID PRIMARY KEY,
    gateway_name STRING,
    api_key STRING,
    webhook_url STRING
);

CREATE TABLE third_party_apis (
    id UUID PRIMARY KEY,
    api_name STRING,
    api_url STRING,
    authentication_token STRING
);

CREATE TABLE payment_logs (
    id UUID PRIMARY KEY,
    log_time TIMESTAMP,
    log_message STRING,
    transaction_id UUID REFERENCES payment_transactions(id)
);

CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  name VARCHAR,
  email VARCHAR,
  phone_number VARCHAR
);

CREATE TABLE payment_gateways (
  gateway_id INT PRIMARY KEY,
  gateway_name VARCHAR,
  currency VARCHAR
);

CREATE TABLE third_party_apis (
  api_id INT PRIMARY KEY,
  api_name VARCHAR,
  endpoint VARCHAR
);

CREATE TABLE transaction_logs (
  log_id INT PRIMARY KEY,
  transaction_id INT,
  log_message TEXT,
  log_timestamp TIMESTAMP
);
