);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50),
    email VARCHAR(50),
    phone_number VARCHAR(15)
);

CREATE TABLE merchants (
  merchant_id UUID PRIMARY KEY,
  merchant_name VARCHAR(255),
  merchant_email VARCHAR(255),
  country VARCHAR(50)
);

CREATE TABLE users (
  user_id UUID PRIMARY KEY,
  user_name VARCHAR(255),
  user_email VARCHAR(255),
  country VARCHAR(50)
);

CREATE TABLE Merchants (merchant_id UUID PRIMARY KEY,
  merchant_name VARCHAR(255) NOT NULL,
  merchant_email VARCHAR(255) NOT NULL,
  merchant_phone VARCHAR(20) NOT NULL
);

CREATE TABLE Transactions (transaction_id UUID PRIMARY KEY,
  transaction_date TIMESTAMP NOT NULL,
  transaction_amount DECIMAL(10, 2) NOT NULL,
  merchant_id UUID REFERENCES Merchants(merchant_id) NOT NULL,
  user_id UUID REFERENCES Users(user_id) NOT NULL
);

CREATE TABLE Users (user_id UUID PRIMARY KEY,
  user_name VARCHAR(255) NOT NULL,
  user_email VARCHAR(255) NOT NULL,
  user_phone VARCHAR(20) NOT NULL
);

CREATE TABLE Wallets (wallet_id UUID PRIMARY KEY,
  user_id UUID REFERENCES Users(user_id) NOT NULL,
  balance DECIMAL(10, 2) NOT NULL
);

CREATE TABLE Currency (currency_id UUID PRIMARY KEY,
  currency_code VARCHAR(3) NOT NULL,
  currency_name VARCHAR(50) NOT NULL
);
