	customer_id INT,
	loyalty_points INT,
	payment_amount FLOAT,
	payment_date DATE
);

CREATE TABLE loyalty_program_data (
    loyalty_id INT PRIMARY KEY,
    customer_id INT,
    loyalty_points INT,
    last_transaction_date DATE
);

CREATE TABLE customer_payment_data (
    payment_id INT PRIMARY KEY,
    customer_id INT,
    payment_amount DECIMAL,
    payment_date DATE
);

CREATE TABLE loyalty_program_data (
   customer_id INT,
   loyalty_points INT,
   membership_level VARCHAR(255)
);

CREATE TABLE payment_data (
   customer_id INT,
   payment_amount DECIMAL(10, 2),
   payment_date DATE
);

CREATE TABLE targeted_promotions (
   promotion_id INT,
   promotion_name VARCHAR(255),
   min_purchase_amount DECIMAL(10, 2)
);

