tables.table_schema:
CREATE TABLE encryption_keys (
	key_id INT PRIMARY KEY,
	encryption_key STRING
);

CREATE TABLE payment_logs (
    log_id UUID PRIMARY KEY,
    payment_id UUID REFERENCES payment_information(payment_id),
    log_description TEXT,
    log_date TIMESTAMP
);

CREATE TABLE payment_users (
  user_id SERIAL PRIMARY KEY,
  username VARCHAR(50) UNIQUE,
  encrypted_card_number VARCHAR(100),
  encrypted_card_expiry VARCHAR(20)
);

CREATE TABLE payment_transactions (
  transaction_id SERIAL PRIMARY KEY,
  user_id INT REFERENCES payment_users(user_id),
  transaction_amount DECIMAL(10, 2),
  timestamp TIMESTAMPTZ
);

CREATE TABLE payment_methods (
  method_id SERIAL PRIMARY KEY,
  user_id INT REFERENCES payment_users(user_id),
  payment_method_type VARCHAR(20),
  encrypted_method_details VARCHAR(100)
);

CREATE TABLE payment_logs (
  log_id SERIAL PRIMARY KEY,
  user_id INT REFERENCES payment_users(user_id),
  log_message TEXT,
  log_timestamp TIMESTAMPTZ
);

CREATE TABLE payment_tokens (
  token_id SERIAL PRIMARY KEY,
  user_id INT REFERENCES payment_users(user_id),
  token_value UUID,
  token_expiry TIMESTAMPTZ
);
