tables.table_schema:
CREATE TABLE user_activity (
    user_id INT,
    activity_type VARCHAR(50),
    timestamp TIMESTAMP,
    CONSTRAINT pk_user_activity PRIMARY KEY (user_id, activity_type)
);

CREATE TABLE user_activity (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    biometric_data BYTEA,
    login_timestamp TIMESTAMP
);

CREATE TABLE behavior_patterns (
    pattern_id INT PRIMARY KEY,
    user_id INT REFERENCES user_activity(user_id),
    pattern_data JSON
);

CREATE TABLE unauthorized_access (
    access_id INT PRIMARY KEY,
    user_id INT REFERENCES user_activity(user_id),
    access_timestamp TIMESTAMP
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    biometric_data JSONB
);

CREATE TABLE access_logs (
    log_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    timestamp TIMESTAMP NOT NULL,
    access_type VARCHAR(10) NOT NULL
);

CREATE TABLE behavior_patterns (
    pattern_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    pattern_details JSONB
);

CREATE TABLE unauthorized_access_attempts (
    attempt_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE users (
  id INT PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  biometric_data JSONB
);

CREATE TABLE login_attempts (
  user_id INT REFERENCES users(id),
  login_time TIMESTAMP NOT NULL,
  login_result BOOLEAN
);

CREATE TABLE access_logs (
  user_id INT REFERENCES users(id),
  access_time TIMESTAMP NOT NULL,
  location VARCHAR(100),
  success BOOLEAN
);

CREATE TABLE behavior_patterns (
  user_id INT REFERENCES users(id),
  pattern_type VARCHAR(50) NOT NULL,
  pattern_data JSONB
);

CREATE TABLE security_alerts (
  user_id INT REFERENCES users(id),
  alert_time TIMESTAMP NOT NULL,
  alert_message TEXT
);
