tables.table_schema:
CREATE TABLE biometric_templates (
    id INT PRIMARY KEY,
    modality VARCHAR(50) NOT NULL,
    template_data BYTEA
);

CREATE TABLE biometric_templates (
    id UUID PRIMARY KEY,
    modality VARCHAR(50) NOT NULL,
    template BYTEA NOT NULL
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    modality VARCHAR(50) NOT NULL REFERENCES biometric_templates(modality)
);

CREATE TABLE biometric_templates (
    template_id INT PRIMARY KEY,
    modality VARCHAR(50),
    template_data BYTEA
);

CREATE TABLE iris_scans (
    iris_scan_id INT PRIMARY KEY,
    template_id INT,
    iris_data BYTEA,
    FOREIGN KEY (template_id) REFERENCES biometric_templates (template_id)
);

CREATE TABLE voice_recognition (
    voice_id INT PRIMARY KEY,
    template_id INT,
    voice_data BYTEA,
    FOREIGN KEY (template_id) REFERENCES biometric_templates (template_id)
);

CREATE TABLE biometric_templates_iris (
    id INT NOT NULL,
    iris_data BYTEA,
    PRIMARY KEY (id)
);

CREATE TABLE biometric_templates_voice (
    id INT NOT NULL,
    voice_sample BYTEA,
    PRIMARY KEY (id)
);

CREATE TABLE biometric_templates_hand_geometry (
    id INT NOT NULL,
    hand_geometry_data BYTEA,
    PRIMARY KEY (id)
);

CREATE TABLE biometric_types (
    type_id INT NOT NULL,
    modality_name VARCHAR(50) NOT NULL,
    PRIMARY KEY (type_id)
);

CREATE TABLE iris_scans (
  iris_id UUID PRIMARY KEY,
  iris_template BYTEA
);

CREATE TABLE voice_recognition (
  voice_id UUID PRIMARY KEY,
  voice_template BYTEA
);

CREATE TABLE hand_geometry (
  hand_id UUID PRIMARY KEY,
  hand_template BYTEA
);

CREATE TABLE fingerprints (
  fingerprint_id UUID PRIMARY KEY,
  fingerprint_template BYTEA
);

CREATE TABLE face_recognition (
  face_id UUID PRIMARY KEY,
  face_template BYTEA
);
