tables.table_schema:
CREATE TABLE wallet (
    id INT PRIMARY KEY,
    user_id INT,
    balance DECIMAL
);

CREATE TABLE user (
    id INT PRIMARY KEY,
    username VARCHAR,
    email VARCHAR
);

CREATE TABLE user_accounts (
 id INT PRIMARY KEY,
 username VARCHAR(50) NOT NULL,
 balance DECIMAL(10, 2) NOT NULL
);

CREATE TABLE transaction_logs (
 transaction_id INT PRIMARY KEY,
 user_id INT NOT NULL,
 amount DECIMAL(10, 2) NOT NULL,
 transaction_time TIMESTAMP NOT NULL
);

CREATE TABLE suspicious_transactions (
 transaction_id INT PRIMARY KEY,
 user_id INT NOT NULL,
 amount DECIMAL(10, 2) NOT NULL,
 alert_message TEXT NOT NULL
);

CREATE TABLE transaction_triggers (
 trigger_id INT PRIMARY KEY,
 trigger_name VARCHAR(50) NOT NULL,
 trigger_conditions JSON NOT NULL
);

CREATE TABLE accounts (
    account_id INT PRIMARY KEY,
    account_owner VARCHAR(100),
    balance DECIMAL,
    last_transaction_date TIMESTAMP
);

CREATE TABLE suspicious_transactions (
    transaction_id INT PRIMARY KEY,
    suspect_reason VARCHAR(200),
    suspect_level INT,
    suspect_date TIMESTAMP
);

CREATE TABLE triggers (
    trigger_id INT PRIMARY KEY,
    trigger_name VARCHAR(100),
    trigger_condition VARCHAR(500),
    trigger_action VARCHAR(200)
);

CREATE TABLE log_activity (
    log_id INT PRIMARY KEY,
    transaction_id INT,
    log_date TIMESTAMP,
    log_description TEXT
);
