tables.table_schema:
CREATE TABLE wallet_address (
    id UUID PRIMARY KEY,
    address VARCHAR(255) NOT NULL,
    owner_id UUID NOT NULL
);

CREATE TABLE wallet_owner (
    id UUID PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE
);

CREATE TABLE wallet_addresses (
    id INT PRIMARY KEY,
    address VARCHAR(255) NOT NULL,
    owner_id INT,
    created_at TIMESTAMP
);

CREATE TABLE wallet_owners (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE,
    created_at TIMESTAMP
);

CREATE TABLE address_ownership (
    address_id INT,
    owner_id INT,
    PRIMARY KEY (address_id, owner_id)
);

CREATE TABLE wallet_address (
    address_id SERIAL PRIMARY KEY,
    owner_name TEXT NOT NULL,
    address TEXT UNIQUE NOT NULL
);

CREATE TABLE address_ownership (
    ownership_id SERIAL PRIMARY KEY,
    address_id INT REFERENCES wallet_address(address_id),
    owner_id INT REFERENCES owner(owner_id)
);

CREATE TABLE owner (
    owner_id SERIAL PRIMARY KEY,
    owner_name TEXT UNIQUE NOT NULL
);

CREATE TABLE wallet_addresses (
    wallet_id UUID PRIMARY KEY,
    address VARCHAR(255) NOT NULL,
    owner_name VARCHAR(100) NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE wallet_owners (
    owner_id UUID PRIMARY KEY,
    full_name VARCHAR(150) NOT NULL,
    email VARCHAR(255) NOT NULL UNIQUE,
    phone_number VARCHAR(20) NOT NULL
);

CREATE TABLE wallet_logs (
    log_id UUID PRIMARY KEY,
    transaction_id UUID REFERENCES wallet_transactions(transaction_id),
    log_message TEXT NOT NULL,
    log_date TIMESTAMP NOT NULL
);

CREATE TABLE wallet_balances (
    wallet_id UUID PRIMARY KEY,
    balance DECIMAL(15, 2) NOT NULL,
    last_updated TIMESTAMP NOT NULL
);
