tables.table_schema:
CREATE TABLE BarcodeSystem (
    Barcode_ID INT PRIMARY KEY,
    Product_Name VARCHAR(255) NOT NULL,
    Product_Category VARCHAR(50),
    Quantity INT
);

CREATE TABLE barcodes (
    barcode_id SERIAL PRIMARY KEY,
    product_name VARCHAR(100),
    quantity INT,
    warehouse_id INT
);

CREATE TABLE warehouses (
    warehouse_id SERIAL PRIMARY KEY,
    warehouse_name VARCHAR(100),
    location VARCHAR(100),
    manager_name VARCHAR(100)
);

CREATE TABLE warehouse (
    warehouse_id INT PRIMARY KEY,
    location VARCHAR(50),
    capacity INT
);

CREATE TABLE product (
    product_id INT PRIMARY KEY,
    name VARCHAR(50),
    price DECIMAL(10, 2),
    barcode VARCHAR(20)
);

CREATE TABLE inventory (
    inventory_id INT PRIMARY KEY,
    product_id INT REFERENCES product(product_id),
    warehouse_id INT REFERENCES warehouse(warehouse_id),
    quantity INT
);

CREATE TABLE BarcodeScanner (
  id INT PRIMARY KEY,
  scanner_name VARCHAR(255),
  location VARCHAR(255)
);

CREATE TABLE Product (
  id INT PRIMARY KEY,
  product_name VARCHAR(255),
  category VARCHAR(100),
  price DECIMAL(10,2)
);

CREATE TABLE Warehouse (
  id INT PRIMARY KEY,
  warehouse_name VARCHAR(255),
  location VARCHAR(255),
  manager_name VARCHAR(255)
);

CREATE TABLE Inventory (
  id INT PRIMARY KEY,
  product_id INT,
  warehouse_id INT,
  quantity INT
);

CREATE TABLE Warehouse (
    id INT PRIMARY KEY,
    location VARCHAR(255) NOT NULL,
    capacity INT
);

CREATE TABLE Product (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    barcode VARCHAR(20) UNIQUE,
    price DECIMAL(10, 2)
);

CREATE TABLE Inventory (
    product_id INT REFERENCES Product(id),
    warehouse_id INT REFERENCES Warehouse(id),
    quantity INT,
    PRIMARY KEY (product_id, warehouse_id)
);

CREATE TABLE Employee (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    role VARCHAR(100)
);

CREATE TABLE BarcodeScan (
    id INT PRIMARY KEY,
    product_id INT REFERENCES Product(id),
    scan_time TIMESTAMP,
    scanned_by INT REFERENCES Employee(id)
);
