tables.table_schema:
CREATE TABLE SalesData (
    date DATE,
    product_id INT,
    sales_amount FLOAT
);

CREATE TABLE internal_sales_data (
    date DATE,
    product_id INT,
    sales_amount DECIMAL,
    PRIMARY KEY(date, product_id)
);

CREATE TABLE external_market_data (
    date DATE,
    product_id INT,
    market_price DECIMAL,
    PRIMARY KEY(date, product_id)
);

CREATE TABLE SalesData ( 
    sales_id INT PRIMARY KEY, 
    product_name VARCHAR(255), 
    sales_amount DECIMAL(10,2) 
);

CREATE TABLE MarketData ( 
    market_id INT PRIMARY KEY, 
    market_name VARCHAR(255), 
    market_value DECIMAL(10,2) 
);

CREATE TABLE sales_data (
    sales_id INT PRIMARY KEY,
    product_id INT,
    sales_date DATE,
    sales_amount DECIMAL
);

CREATE TABLE market_data (
    market_id INT PRIMARY KEY,
    market_name VARCHAR(255),
    market_location VARCHAR(255),
    market_category VARCHAR(50)
);

CREATE TABLE integration_table (
    integration_id INT PRIMARY KEY,
    sales_id INT,
    market_id INT
);

CREATE TABLE demand_forecasts (
    forecast_id INT PRIMARY KEY,
    product_id INT,
    forecast_date DATE,
    demand_forecast DECIMAL
);

CREATE TABLE products (product_id UUID PRIMARY KEY,
	product_name TEXT,
	product_category TEXT);

CREATE TABLE markets (market_id UUID PRIMARY KEY,
	market_name TEXT,
	market_location TEXT);
