tables.table_schema:
CREATE TABLE product_demand (
    product_id INT,
    product_name TEXT,
    demand_date DATE,
    demand_quantity INT
);

CREATE TABLE Products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    demand INT NOT NULL,
    time_period DATE NOT NULL
);

CREATE TABLE AverageDemand (
    product_id INT REFERENCES Products(product_id),
    average_demand FLOAT,
    time_period DATE
);

CREATE TABLE products (
  product_id INT PRIMARY KEY,
  product_name TEXT
);

CREATE TABLE demand (
  demand_id INT PRIMARY KEY,
  product_id INT,
  demand_quantity INT,
  demand_date DATE
);

CREATE TABLE average_demand (
  average_demand_id INT PRIMARY KEY,
  product_id INT,
  average_quantity DECIMAL
);

CREATE TABLE products(
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50),
    category VARCHAR(50),
    price DECIMAL(10, 2)
);

CREATE TABLE orders(
    order_id INT PRIMARY KEY,
    product_id INT,
    quantity INT,
    order_date DATE
);

CREATE TABLE customers(
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    address VARCHAR(100),
    phone_number VARCHAR(15)
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    demand INT NOT NULL
);

CREATE TABLE categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255) NOT NULL
);

CREATE TABLE product_categories (
    product_id INT REFERENCES products(product_id),
    category_id INT REFERENCES categories(category_id),
    PRIMARY KEY (product_id, category_id)
);

CREATE TABLE demand_statistics (
    product_id INT REFERENCES products(product_id),
    month_year VARCHAR(10) NOT NULL,
    average_demand DECIMAL(10, 2) NOT NULL,
    PRIMARY KEY (product_id, month_year)
);
