tables.table_schema:
CREATE TABLE table1 (
  id INT PRIMARY KEY,
  date DATE,
  demand INT
);

CREATE TABLE table2 (
  id INT PRIMARY KEY,
  date DATE,
  moving_avg FLOAT,
  demand_trend VARCHAR
);

CREATE TABLE moving_averages (
    id INT PRIMARY KEY,
    demand INT,
    date DATE
);

CREATE TABLE demand_trends (
    id INT PRIMARY KEY,
    product_name VARCHAR(50),
    demand INT,
    year INT
);

CREATE TABLE time_periods (
    id INT PRIMARY KEY,
    period_name VARCHAR(50),
    start_date DATE,
    end_date DATE
);

CREATE TABLE demand_data (
  date DATE,
  product_id INT,
  demand INT
);

CREATE TABLE moving_avg_results (
  date DATE,
  product_id INT,
  moving_avg_demand DECIMAL
);

CREATE TABLE trend_analysis_results (
  date DATE,
  product_id INT,
  demand_trend VARCHAR
);

CREATE TABLE time_periods (
  period_id INT,
  start_date DATE,
  end_date DATE
);
