tables.table_schema:
CREATE TABLE SupplierPerformanceMetrics (
  supplier_id INT,
  on_time_delivery DECIMAL,
  quality DECIMAL
);

CREATE TABLE supplier_performance_metrics (
  supplier_id INT PRIMARY KEY,
  on_time_delivery_metric FLOAT,
  quality_metric FLOAT
);

CREATE TABLE supplier_contacts (
  contact_id INT PRIMARY KEY,
  supplier_id INT,
  contact_name VARCHAR(50),
  email VARCHAR(100)
);

CREATE TABLE supplier_metrics (
    supplier_id INT PRIMARY KEY,
    on_time_delivery_rate FLOAT,
    quality_rating INT
);

CREATE TABLE delivery_history (
    delivery_id INT PRIMARY KEY,
    supplier_id INT,
    delivery_date DATE,
    delivery_status VARCHAR(20)
);

CREATE TABLE quality_metrics (
    quality_id INT PRIMARY KEY,
    supplier_id INT,
    goods_received_date DATE,
    goods_quality_rating INT
);

CREATE TABLE suppliers (
	supplier_id INT PRIMARY KEY,
	supplier_name TEXT NOT NULL,
	phone_number TEXT
);

CREATE TABLE deliveries (
	delivery_id INT PRIMARY KEY,
	delivery_date DATE NOT NULL,
	delivery_quantity INT NOT NULL,
	supplier_id INT REFERENCES suppliers(supplier_id)
);

CREATE TABLE quality_metrics (
	metric_id INT PRIMARY KEY,
	metric_name TEXT NOT NULL,
	metric_value DECIMAL NOT NULL
);

CREATE TABLE Suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    on_time_delivery_rating DECIMAL,
    quality_rating DECIMAL
);

CREATE TABLE Orders (
    order_id INT PRIMARY KEY,
    order_date DATE,
    supplier_id INT,
    total_amount DECIMAL
);

CREATE TABLE Deliveries (
    delivery_id INT PRIMARY KEY,
    order_id INT,
    delivery_date DATE,
    status VARCHAR(50)
);

CREATE TABLE DeliveryPerformance (
    delivery_id INT,
    on_time BOOLEAN,
    quality_rating DECIMAL,
    PRIMARY KEY(delivery_id)
);

CREATE TABLE QualityMetrics (
    supplier_id INT,
    quality_rating DECIMAL,
    performance_date DATE
);
