tables.table_schema:
CREATE TABLE shipping_schedules (
  schedule_id INT PRIMARY KEY,
  carrier_name VARCHAR,
  expected_arrival_date DATE,
  shipment_status VARCHAR
);

CREATE TABLE shipping_schedules (
  schedule_id INT PRIMARY KEY,
  shipment_date DATE,
  destination VARCHAR(50)
);

CREATE TABLE tracking_records (
  record_id INT PRIMARY KEY,
  schedule_id INT,
  status VARCHAR(50)
);

CREATE TABLE ShippingCompanies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    contact_email VARCHAR(255) NOT NULL,
    contact_phone VARCHAR(20) NOT NULL
);

CREATE TABLE DeliveryStatus (
    delivery_id INT PRIMARY KEY,
    schedule_id INT,
    status VARCHAR(50) NOT NULL,
    update_time TIMESTAMP
);

CREATE TABLE ShippingSchedule (
    shipment_id UUID PRIMARY KEY,
    destination_address VARCHAR(255) NOT NULL,
    delivery_date DATE NOT NULL
);

CREATE TABLE ShipmentStatus (
    shipment_id UUID PRIMARY KEY,
    current_status VARCHAR(50) NOT NULL,
    update_timestamp TIMESTAMP
);

CREATE TABLE DeliveryPersonnel (
    personnel_id UUID PRIMARY KEY,
    personnel_name VARCHAR(100) NOT NULL
);

CREATE TABLE DeliveryLog (
    log_id UUID PRIMARY KEY,
    shipment_id UUID REFERENCES ShippingSchedule(shipment_id),
    delivery_date DATE,
    delivery_personnel_id UUID REFERENCES DeliveryPersonnel(personnel_id)
);

CREATE TABLE ShippingCompany (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(50),
    company_location VARCHAR(100)
);

CREATE TABLE ShippingRoute (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(50),
    origin_location VARCHAR(100),
    destination_location VARCHAR(100)
);

CREATE TABLE DeliveryStatus (
    status_id INT PRIMARY KEY,
    status_description VARCHAR(100)
);
