tables.table_schema:
CREATE TABLE inventory (
    id INT PRIMARY KEY,
    product_name TEXT,
    barcode VARCHAR(50) UNIQUE
);

CREATE TABLE scanned_items (
    scan_id SERIAL PRIMARY KEY,
    barcode VARCHAR(50),
    scan_timestamp TIMESTAMP
);

CREATE TABLE InventoryItems (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(255),
    barcode VARCHAR(50) UNIQUE,
    quantity INT
);

CREATE TABLE ScannedItems (
    scan_id SERIAL PRIMARY KEY,
    item_id INT,
    scan_timestamp TIMESTAMP,
    FOREIGN KEY (item_id) REFERENCES InventoryItems(item_id)
);

CREATE TABLE products (
    product_id UUID PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    barcode VARCHAR(50) UNIQUE
);

CREATE TABLE inventory (
    inventory_id UUID PRIMARY KEY,
    product_id UUID REFERENCES products(product_id),
    quantity INTEGER DEFAULT 0
);

CREATE TABLE locations (
    location_id UUID PRIMARY KEY,
    location_name VARCHAR(255) NOT NULL,
    address VARCHAR(255)
);

CREATE TABLE scanning_records (
    scanning_id UUID PRIMARY KEY,
    product_id UUID REFERENCES products(product_id),
    location_id UUID REFERENCES locations(location_id),
    scanning_time TIMESTAMP NOT NULL
);

CREATE TABLE BarcodeScanner (
  id SERIAL PRIMARY KEY,
  scanner_name TEXT NOT NULL,
  manufacturer TEXT,
  purchase_date DATE
);

CREATE TABLE InventoryItem (
  id SERIAL PRIMARY KEY,
  item_name TEXT NOT NULL,
  barcode TEXT UNIQUE,
  description TEXT
);

CREATE TABLE InventoryLocation (
  id SERIAL PRIMARY KEY,
  location_name TEXT NOT NULL,
  address TEXT,
  capacity INT
);

CREATE TABLE InventoryAudit (
  id SERIAL PRIMARY KEY,
  audit_date DATE,
  auditor_name TEXT,
  discrepancy_found BOOLEAN
);
