tables.table_schema:
CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    category VARCHAR(50),
    price DECIMAL(10, 2)
);

CREATE TABLE categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50)
);

CREATE TABLE category (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(255) NOT NULL
);

CREATE TABLE product (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(255) NOT NULL,
  category_id INT,
  FOREIGN KEY (category_id) REFERENCES category(category_id)
);

CREATE TABLE storage_layout (
  layout_id INT PRIMARY KEY,
  product_id INT,
  storage_location VARCHAR(255) NOT NULL,
  FOREIGN KEY (product_id) REFERENCES product(product_id)
);

CREATE TABLE category (id INT PRIMARY KEY, name VARCHAR(255));
CREATE TABLE product (id INT PRIMARY KEY, name VARCHAR(255), category_id INT, price DECIMAL(10, 2));
CREATE TABLE storage (id INT PRIMARY KEY, location VARCHAR(255), capacity DECIMAL(10, 2));
CREATE TABLE product_category_mapping (product_id INT, category_id INT);
CREATE TABLE optimization_strategy (id INT PRIMARY KEY, strategy_name VARCHAR(255), description TEXT);
