tables.table_schema:
CREATE TABLE transportation_costs (
    cost_id INT PRIMARY KEY,
    transport_mode VARCHAR(50),
    cost_amount DECIMAL(10,2),
    route_id INT,
    date_expense DATE
);

CREATE TABLE cost_saving_opportunities (
    opportunity_id INT PRIMARY KEY,
    saving_area VARCHAR(50),
    suggested_method VARCHAR(100),
    potential_savings DECIMAL(10,2)
);

CREATE TABLE historical_transportation_costs (
id INT PRIMARY KEY,
transportation_mode VARCHAR,
transportation_cost DECIMAL
);

CREATE TABLE cost_saving_opportunities (
id INT PRIMARY KEY,
cost_saving_strategy TEXT,
saving_estimation DECIMAL
);

CREATE TABLE transportation_routes (
id INT PRIMARY KEY,
route_name VARCHAR,
route_type VARCHAR,
route_distance DECIMAL
);
