tables.table_schema:
CREATE TABLE customer_behavior (
  customer_id INT PRIMARY KEY,
  behavior_type VARCHAR(50),
  behavior_rating INT
);

CREATE TABLE transportation_demand (
  demand_id INT PRIMARY KEY,
  transportation_type VARCHAR(50),
  demand_amount INT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    customer_email VARCHAR(50),
    customer_phone VARCHAR(20)
);

CREATE TABLE demand_forecast (
    forecast_id INT PRIMARY KEY,
    forecast_date DATE,
    demand_estimate INT
);

CREATE TABLE customer_behaviour (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    purchase_history JSON
);

CREATE TABLE transportation_demand (
    demand_id INT PRIMARY KEY,
    demand_date DATE,
    demand_location VARCHAR(100),
    demand_quantity INT
);

CREATE TABLE pricing_strategy (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(50),
    strategy_details TEXT
);
