tables.table_schema:
CREATE TABLE vehicle_locations (
    vehicle_id UUID PRIMARY KEY,
    location_point GEOGRAPHY(POINT),
    last_updated TIMESTAMP
);

CREATE TABLE resource_utilization (
    resource_id UUID PRIMARY KEY,
    vehicle_id UUID REFERENCES vehicle_locations(vehicle_id),
    status ENUM('active', 'inactive'),
    utilization_percent DECIMAL
);

CREATE TABLE vehicles (
    vehicle_id INT PRIMARY KEY,
    license_plate VARCHAR(10),
    vehicle_type VARCHAR(20),
    last_known_location VARCHAR(50)
);

CREATE TABLE drivers (
    driver_id INT PRIMARY KEY,
    driver_name VARCHAR(30),
    driver_license VARCHAR(20),
    contact_info VARCHAR(50)
);

CREATE TABLE drivers (
    driver_id UUID PRIMARY KEY,
    driver_name VARCHAR(255) NOT NULL,
    contact_number VARCHAR(15) NOT NULL,
    vehicle_id UUID REFERENCES vehicles (vehicle_id) ON DELETE CASCADE
);

CREATE TABLE vehicles (
    vehicle_id INT PRIMARY KEY,
    vehicle_name VARCHAR(50),
    vehicle_type VARCHAR(50),
    last_known_location VARCHAR(100)
);

CREATE TABLE drivers (
    driver_id INT PRIMARY KEY,
    driver_name VARCHAR(50),
    driver_license VARCHAR(20),
    phone_number VARCHAR(15)
);

CREATE TABLE routes (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(50),
    start_location VARCHAR(100),
    end_location VARCHAR(100)
);

CREATE TABLE assigned_tasks (
    task_id INT PRIMARY KEY,
    vehicle_id INT,
    driver_id INT,
    route_id INT,
    task_status VARCHAR(20)
);

CREATE TABLE maintenance_logs (
    log_id INT PRIMARY KEY,
    vehicle_id INT,
    maintenance_type VARCHAR(50),
    maintenance_date DATE
);
