tables.table_schema:
CREATE TABLE VendorPerformanceData (
    vendor_id INT PRIMARY KEY,
    vendor_name VARCHAR,
    performance_score INT
);

CREATE TABLE SupplierRelationships (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR,
    relationship_status VARCHAR
);

CREATE TABLE supplier_relationships (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(50),
    contact_person VARCHAR(50),
    contact_email VARCHAR(50)
);

CREATE TABLE delivery_tracking (
    tracking_id UUID PRIMARY KEY,
    order_id UUID REFERENCES purchase_orders(order_id),
    delivery_date DATE,
    delivery_status VARCHAR(50)
);

CREATE TABLE payment_records (
    payment_id UUID PRIMARY KEY,
    vendor_id UUID REFERENCES vendor_performance_data(vendor_id),
    payment_date DATE,
    amount DECIMAL
);
