tables.table_schema:
CREATE TABLE purchase_requests (
    id UUID PRIMARY KEY,
    requester_name VARCHAR(255),
    request_date TIMESTAMP,
    total_amount DECIMAL
);

CREATE TABLE approval_workflow (
    approval_id UUID PRIMARY KEY,
    request_id UUID REFERENCES purchase_requests(id),
    approver_name VARCHAR(255),
    approval_date TIMESTAMP
);

CREATE TABLE approval_logs (
    log_id UUID PRIMARY KEY,
    approval_id UUID REFERENCES approval_workflow(approval_id),
    action_performed VARCHAR(255),
    action_date TIMESTAMP
);

CREATE TABLE purchase_requests (
  id UUID PRIMARY KEY,
  request_date DATE,
  total_amount DECIMAL,
  status VARCHAR(50)
);

CREATE TABLE approvers (
  id UUID PRIMARY KEY,
  name VARCHAR(100),
  email VARCHAR(255),
  role VARCHAR(50)
);

CREATE TABLE PurchaseRequests (
	request_id INT PRIMARY KEY,
	requester_name VARCHAR(50),
	request_date DATE,
	approval_status VARCHAR(20)
);

CREATE TABLE Approvers (
	approver_id INT PRIMARY KEY,
	approver_name VARCHAR(50),
	email_address VARCHAR(100)
);

CREATE TABLE ApprovalHistory (
	history_id INT PRIMARY KEY,
	request_id INT,
	approver_id INT,
	approval_date DATE
);

CREATE TABLE PurchaseStatus (
	status_id INT PRIMARY KEY,
	status_name VARCHAR(50)
);
