tables.table_schema:
CREATE TABLE product_certifications (
    product_id INT PRIMARY KEY,
    certification_type VARCHAR(50),
    certification_date DATE
);

CREATE TABLE certification_agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(50),
    contact_person VARCHAR(50),
    contact_email VARCHAR(100)
);

CREATE TABLE ProductCertifications (
    id INT PRIMARY KEY,
    product_name TEXT,
    certification TEXT
);

CREATE TABLE CertifyingOrganizations (
    id INT PRIMARY KEY,
    org_name TEXT,
    country TEXT
);

CREATE TABLE product_certifications(
	certification_id INT PRIMARY KEY,
	product_id INT,
	certification_name VARCHAR(50),
	certification_date DATE
);

CREATE TABLE products(
	product_id INT PRIMARY KEY,
	product_name VARCHAR(50),
	manufacturer VARCHAR(50),
	product_type VARCHAR(50)
);

CREATE TABLE certification_agencies(
	agency_id INT PRIMARY KEY,
	agency_name VARCHAR(50),
	agency_location VARCHAR(50)
);

CREATE TABLE product_certification_relationship(
	product_id INT,
	certification_id INT,
	agency_id INT,
	certification_expiry_date DATE
);

CREATE TABLE CertificationAuthorities (
    authority_id UUID PRIMARY KEY,
    authority_name VARCHAR(100) NOT NULL,
    country VARCHAR(50)
);

CREATE TABLE ProductCategories (
    category_id UUID PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL
);

CREATE TABLE ProductManufacturers (
    manufacturer_id UUID PRIMARY KEY,
    manufacturer_name VARCHAR(100) NOT NULL,
    country VARCHAR(50)
);
