tables.table_schema:
CREATE TABLE supplier_details (
    supplier_id INT PRIMARY KEY,
    supplier_name TEXT,
    supplier_email TEXT
);

CREATE TABLE payment_history (
    payment_id INT PRIMARY KEY,
    supplier_id INT,
    payment_amount DECIMAL,
    payment_date DATE
);

CREATE TABLE late_payments (
    payment_id INT,
    late_days INT,
    late_reason TEXT
);

CREATE TABLE payment_flag (
    payment_id INT,
    flag_status BOOLEAN
);
