tables.table_schema:
CREATE TABLE geopolitical_events (
    event_id SERIAL PRIMARY KEY,
    event_name TEXT,
    event_date DATE,
    event_location TEXT
);

CREATE TABLE geopolitical_events (
    event_id INT PRIMARY KEY,
    event_date DATE NOT NULL,
    event_description TEXT NOT NULL,
    supply_chain_impact TEXT
);

CREATE TABLE countries (
    country_id INT PRIMARY KEY,
    country_name VARCHAR(50) NOT NULL
);

CREATE TABLE supply_chain_routes (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(50) NOT NULL,
    source_country_id INT REFERENCES countries(country_id),
    destination_country_id INT REFERENCES countries(country_id)
);

CREATE TABLE risk_mitigation_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_description TEXT NOT NULL,
    is_active BOOLEAN DEFAULT TRUE
);
