tables.table_schema:
CREATE TABLE SupplyChainIncidents (
  incident_id INT PRIMARY KEY,
  incident_date DATE,
  description TEXT,
  resolution TEXT
);

CREATE TABLE IncidentCategories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(50)
);

CREATE TABLE SupplyChainIncidents (
    incident_id INT PRIMARY KEY,
    incident_date DATE,
    description TEXT,
    resolution TEXT
);

CREATE TABLE IncidentCauses (
    cause_id INT PRIMARY KEY,
    incident_id INT,
    cause_description TEXT,
    FOREIGN KEY (incident_id) REFERENCES SupplyChainIncidents(incident_id)
);

CREATE TABLE IncidentLocations (
    location_id INT PRIMARY KEY,
    incident_id INT,
    location_description TEXT,
    FOREIGN KEY (incident_id) REFERENCES SupplyChainIncidents(incident_id)
);

CREATE TABLE incidents (
    incident_id UUID PRIMARY KEY,
    incident_date DATE,
    incident_description TEXT,
    resolved BOOLEAN
);

CREATE TABLE resolution_actions (
    action_id UUID PRIMARY KEY,
    resolution_description TEXT,
    completion_date DATE,
    incident_id UUID REFERENCES incidents(incident_id)
);

CREATE TABLE incident_categories (
    category_id UUID PRIMARY KEY,
    category_name VARCHAR(50) UNIQUE
);

CREATE TABLE incident_assignees (
    assignee_id UUID PRIMARY KEY,
    assignee_name VARCHAR(50)
);
