tables.table_schema:
CREATE TABLE carbon_footprint_data (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(50),
    carbon_intensity DECIMAL
);

CREATE TABLE sourcing_decisions (
    decision_id INT PRIMARY KEY,
    material_id INT,
    decision_date DATE,
    decision_type VARCHAR(50)
);

CREATE TABLE raw_materials (
    material_id UUID PRIMARY KEY,
    material_name VARCHAR(255),
    carbon_intensity FLOAT
);

CREATE TABLE suppliers (
    supplier_id UUID PRIMARY KEY,
    supplier_name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE RawMaterials (
    material_id SERIAL PRIMARY KEY,
    material_name VARCHAR(100) NOT NULL,
    carbon_footprint DECIMAL(10,2) NOT NULL
);

CREATE TABLE Suppliers (
    supplier_id SERIAL PRIMARY KEY,
    supplier_name VARCHAR(100) NOT NULL
);

CREATE TABLE MaterialSuppliers (
    material_id INT NOT NULL,
    supplier_id INT NOT NULL,
    FOREIGN KEY (material_id) REFERENCES RawMaterials(material_id),
    FOREIGN KEY (supplier_id) REFERENCES Suppliers(supplier_id)
);

CREATE TABLE CarbonFootprintData (
  id INT PRIMARY KEY,
  material_name VARCHAR(255),
  carbon_emission DECIMAL(10, 2),
  source_location VARCHAR(255)
);

CREATE TABLE MaterialDetails (
  id INT PRIMARY KEY,
  material_name VARCHAR(255),
  description TEXT
);

CREATE TABLE SourceLocation (
  id INT PRIMARY KEY,
  location_name VARCHAR(255),
  coordinates VARCHAR(50)
);

CREATE TABLE SustainableSourcingDecisions (
  id INT PRIMARY KEY,
  decision_date DATE,
  decision_description TEXT
);
