tables.table_schema:
CREATE TABLE energy_consumption (
    process_id BIGINT,
    timestamp TIMESTAMP,
    energy_value DECIMAL,
    location_id BIGINT
);

CREATE TABLE energy_consumption (
    process_id INT PRIMARY KEY,
    process_name VARCHAR(255),
    energy_usage DECIMAL(10,2),
    time_stamp TIMESTAMP
);

CREATE TABLE energy_consumption (
    process_id SERIAL PRIMARY KEY,
    process_name VARCHAR(100) NOT NULL,
    energy_type VARCHAR(50) NOT NULL,
    energy_usage DECIMAL NOT NULL
);

CREATE TABLE process_location (
    process_id SERIAL PRIMARY KEY,
    location_name VARCHAR(100) NOT NULL,
    country_code CHAR(2) NOT NULL
);

CREATE TABLE energy_savings (
    saving_id SERIAL PRIMARY KEY,
    process_id INT REFERENCES energy_consumption(process_id),
    saving_amount DECIMAL NOT NULL
);

CREATE TABLE energy_consumption_data (
    process_id SERIAL PRIMARY KEY,
    process_name VARCHAR(50) NOT NULL,
    energy_consumed DECIMAL(10,2) NOT NULL,
    timestamp TIMESTAMPTZ NOT NULL
);

CREATE TABLE manufacturing_processes (
    process_id SERIAL PRIMARY KEY,
    process_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL,
    manager VARCHAR(50) NOT NULL
);

CREATE TABLE supply_chain_operations (
    operation_id SERIAL PRIMARY KEY,
    operation_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL,
    operation_type VARCHAR(50) NOT NULL
);

CREATE TABLE manufacturing_processes (
    id INT PRIMARY KEY,
    process_name TEXT,
    energy_consumption DECIMAL
);

CREATE TABLE supply_chain_operations (
    id INT PRIMARY KEY,
    operation_name TEXT,
    operation_type TEXT
);

CREATE TABLE energy_consumption_data (
    id INT PRIMARY KEY,
    process_id INT,
    operation_id INT,
    consumption_value DECIMAL,
    recorded_date DATE
);

CREATE TABLE energy_consumption_summary (
    id INT PRIMARY KEY,
    process_id INT,
    total_consumption DECIMAL,
    month_year DATE
);

CREATE TABLE energy_improvement_areas (
    id INT PRIMARY KEY,
    process_id INT,
    improvement_area TEXT
);
