tables.table_schema:
CREATE TABLE WasteCollection (
    waste_id INT PRIMARY KEY,
    waste_type VARCHAR(255),
    collection_date DATE,
    collection_weight DECIMAL
);

CREATE TABLE waste_collection (
  collection_id UUID PRIMARY KEY,
  collection_date DATE NOT NULL,
  collection_weight FLOAT NOT NULL
);

CREATE TABLE waste_recycling (
  recycling_id UUID PRIMARY KEY,
  recycling_date DATE NOT NULL,
  recycled_material VARCHAR(50) NOT NULL,
  quantity_recycled INT NOT NULL
);

CREATE TABLE waste_disposal (
  disposal_id UUID PRIMARY KEY,
  disposal_date DATE NOT NULL,
  disposal_location VARCHAR(100) NOT NULL
);

CREATE TABLE WasteFacilities (
    facility_id UUID PRIMARY KEY,
    facility_name TEXT NOT NULL,
    location TEXT,
    capacity INT
);

CREATE TABLE WasteTypes (
    type_id UUID PRIMARY KEY,
    waste_type TEXT NOT NULL,
    recyclable BOOLEAN
);

CREATE TABLE RecyclingCenters (
    center_id UUID PRIMARY KEY,
    center_name TEXT NOT NULL,
    location TEXT,
    capacity INT
);
