tables.table_schema:
CREATE TABLE supply_chain_incidents (
    incident_id INT PRIMARY KEY,
    incident_date DATE,
    location VARCHAR(255),
    description TEXT
);

CREATE TABLE incident_data (
    incident_id INT PRIMARY KEY,
    incident_date DATE,
    description TEXT
);

CREATE TABLE supply_chain (
    supply_chain_id INT PRIMARY KEY,
    product_name TEXT,
    supplier TEXT,
    disruption_reason TEXT
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name TEXT,
    category TEXT,
    description TEXT
);
