tables.table_schema:
CREATE TABLE patient_demographics (
id INT,
name VARCHAR(255),
age INT,
address VARCHAR(255)
);

CREATE TABLE medical_history (
id INT,
patient_id INT,
medical_condition VARCHAR(255),
diagnosis_date DATE
);

CREATE TABLE treatment_data (
id INT,
patient_id INT,
treatment VARCHAR(255),
treatment_date DATE
);

CREATE TABLE treatment_data (
    treatment_id SERIAL PRIMARY KEY,
    patient_id INTEGER REFERENCES patient_demographics(patient_id),
    treatment_description TEXT,
    treatment_date DATE
);

CREATE TABLE physicians (
    physician_id SERIAL PRIMARY KEY,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    specialization VARCHAR(50)
);

CREATE TABLE PatientDemographics (
 patient_id INT PRIMARY KEY,
 name VARCHAR,
 age INT,
 gender VARCHAR
);

CREATE TABLE MedicalHistory (
 patient_id INT PRIMARY KEY,
 diagnosis VARCHAR,
 date_diagnosed DATE,
 doctor VARCHAR
);

CREATE TABLE TreatmentData (
 treatment_id INT PRIMARY KEY,
 patient_id INT,
 treatment_description VARCHAR,
 treatment_date DATE
);

CREATE TABLE Allergies (
 patient_id INT,
 allergen VARCHAR
);

CREATE TABLE Medications (
 medication_id INT PRIMARY KEY,
 patient_id INT,
 medication_name VARCHAR,
 dosage VARCHAR
);
