tables.table_schema:
CREATE TABLE medications ( 
    medication_id INT PRIMARY KEY, 
    medication_name VARCHAR(255) NOT NULL, 
    inventory_level INT NOT NULL, 
    expiration_date DATE NOT NULL 
);

CREATE TABLE restocking_logs ( 
    log_id INT PRIMARY KEY, 
    medication_id INT NOT NULL, 
    restocking_date DATE NOT NULL, 
    quantity_restocked INT NOT NULL 
);

CREATE TABLE Medications (
  id SERIAL PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  quantity INTEGER NOT NULL,
  expiration_date DATE NOT NULL
);

CREATE TABLE Expirations (
  id SERIAL PRIMARY KEY,
  medication_id INTEGER REFERENCES Medications(id),
  expiration_date DATE NOT NULL,
  days_left_until_expired INTEGER
);

CREATE TABLE Medications (
  id UUID PRIMARY KEY,
  name STRING,
  quantity INT,
  expiration_date DATE
);

CREATE TABLE Suppliers (
  id UUID PRIMARY KEY,
  name STRING,
  contact_person STRING,
  email STRING
);

CREATE TABLE Orders (
  id UUID PRIMARY KEY,
  medication_id UUID REFERENCES Medications(id),
  order_date DATE,
  quantity INT
);

CREATE TABLE RestockingLogs (
  id UUID PRIMARY KEY,
  order_id UUID REFERENCES Orders(id),
  restock_date DATE,
  restock_quantity INT
);

CREATE TABLE medication_inventory (
    id UUID PRIMARY KEY,
    medication_name STRING,
    quantity INT,
    expiration_date DATE
);

CREATE TABLE medication_locations (
    location_id UUID PRIMARY KEY,
    location_name STRING,
    address STRING
);

CREATE TABLE medication_suppliers (
    supplier_id UUID PRIMARY KEY,
    supplier_name STRING,
    contact_email STRING
);

CREATE TABLE medication_audit_log (
    log_id UUID PRIMARY KEY,
    action_type STRING,
    user_id UUID,
    action_timestamp TIMESTAMP
);
