tables.table_schema:
CREATE TABLE imaging_technicians (
  technician_id INT PRIMARY KEY,
  technician_name VARCHAR(50) NOT NULL,
  technician_contact VARCHAR(20),
  technician_specialization VARCHAR(50)
);

CREATE TABLE Imaging_Images (
    image_id UUID PRIMARY KEY,
    report_id UUID REFERENCES Imaging_Reports(report_id),
    image_type VARCHAR(50),
    image_url VARCHAR(255)
);

CREATE TABLE Physicians(
  physician_id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  specialization VARCHAR(50)
);
