tables.table_schema:
CREATE TABLE patient_images (
  id SERIAL PRIMARY KEY,
  patient_id INT,
  image_data BYTEA,
  imaging_timepoint TIMESTAMP
);

CREATE TABLE treatment_status (
  id SERIAL PRIMARY KEY,
  patient_id INT,
  treatment_start_date DATE,
  treatment_effectiveness TEXT
);

CREATE TABLE imaging_data_1 (
    id INT PRIMARY KEY,
    image_path VARCHAR(255),
    time_point_1 DATE,
    treatment_effectiveness FLOAT
);

CREATE TABLE imaging_data_2 (
    id INT PRIMARY KEY,
    image_path VARCHAR(255),
    time_point_2 DATE,
    treatment_effectiveness FLOAT
);

CREATE TABLE imaging_data_timepoint1 (
    id UUID PRIMARY KEY,
    patient_id UUID,
    imaging_date TIMESTAMP,
    imaging_results JSONB
);

CREATE TABLE imaging_data_timepoint2 (
    id UUID PRIMARY KEY,
    patient_id UUID,
    imaging_date TIMESTAMP,
    imaging_results JSONB
);

CREATE TABLE treatment_data (
    id UUID PRIMARY KEY,
    patient_id UUID,
    treatment_date TIMESTAMP,
    treatment_details JSONB
);

CREATE TABLE treatment_effectiveness (
    id UUID PRIMARY KEY,
    patient_id UUID,
    treatment_outcome JSONB,
    effectiveness_score INT
);

CREATE TABLE ImagingData_001 (
  patient_id INT,
  time_point_001_date DATE,
  imaging_results_001 TEXT
);

CREATE TABLE ImagingData_002 (
  patient_id INT,
  time_point_002_date DATE,
  imaging_results_002 TEXT
);

CREATE TABLE TreatmentDetails (
  treatment_id INT,
  treatment_name VARCHAR(50),
  treatment_duration VARCHAR(20),
  treatment_outcome TEXT
);

CREATE TABLE ComparativeResults (
  patient_id INT,
  improvement_status VARCHAR(20),
  compare_date DATE
);
