tables.table_schema:
CREATE TABLE imaging_data (
    patient_id UUID PRIMARY KEY,
    imaging_date DATE,
    modality VARCHAR,
    image_file BYTEA
);

CREATE TABLE health_records (
    patient_id UUID PRIMARY KEY,
    record_date DATE,
    diagnosis VARCHAR,
    treatment_plan TEXT
);

CREATE TABLE physicians (
  physician_id UUID PRIMARY KEY,
  physician_name VARCHAR(50),
  specialization VARCHAR(50),
  hospital_name VARCHAR(50)
);

CREATE TABLE ImagingData (
  id INT PRIMARY KEY,
  patient_id INT,
  image_url TEXT,
  date_added TIMESTAMP
);

CREATE TABLE HealthRecords (
  id INT PRIMARY KEY,
  patient_id INT,
  medical_condition TEXT,
  diagnosis_date DATE
);

CREATE TABLE Patients (
  id INT PRIMARY KEY,
  name TEXT,
  date_of_birth DATE,
  gender VARCHAR(10)
);

CREATE TABLE ImagingReports (
  id INT PRIMARY KEY,
  image_id INT,
  report_text TEXT,
  report_date DATE
);

CREATE TABLE AppointmentSchedule (
  id INT PRIMARY KEY,
  patient_id INT,
  appointment_date DATE,
  notes TEXT
);
