tables.table_schema:
CREATE TABLE diagnosis_history (
    diagnosis_id INT PRIMARY KEY,
    patient_id INT REFERENCES patient_personal_information(patient_id),
    diagnosis_date DATE,
    diagnosis_description TEXT
);

CREATE TABLE treatment_history (
    treatment_id INT PRIMARY KEY,
    diagnosis_id INT REFERENCES diagnosis_history(diagnosis_id),
    treatment_date DATE,
    treatment_description TEXT
);
