CREATE TABLE patient_prescriptions (
    prescription_id INT PRIMARY KEY,
    patient_id INT,
    medication_name VARCHAR(50),
    dosage FLOAT
);

CREATE TABLE dosage_schedule (
    schedule_id INT PRIMARY KEY,
    prescription_id INT,
    schedule_time TIME,
    quantity INT
);

CREATE TABLE patients (
  patient_id INT PRIMARY KEY,
