tables.table_schema:
CREATE TABLE billing_analysis (
    customer_id INT,
    transaction_date DATE,
    transaction_amount DECIMAL(10, 2),
    billing_description TEXT
);

CREATE TABLE abnormal_billing_practices (
    id INT PRIMARY KEY,
    customer_id INT,
    reason VARCHAR,
    timestamp TIMESTAMP
);

CREATE TABLE BillingPatterns (
    id INT,
    user_id INT,
    amount DECIMAL,
    transaction_date DATE
);

CREATE TABLE AbnormalBilling (
    id INT,
    user_id INT,
    reason TEXT,
    flagged_date DATE
);

CREATE TABLE BillingAnalysis (
    id INT,
    user_id INT,
    analysis_result TEXT,
    analyzed_date DATE
);

CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    customer_email VARCHAR(255)
);

CREATE TABLE Invoices (
    invoice_id INT PRIMARY KEY,
    customer_id INT,
    invoice_amount DECIMAL(10, 2),
    invoice_date DATE
);

CREATE TABLE BillingHistory (
    invoice_id INT,
    payment_amount DECIMAL(10, 2),
    payment_date DATE
);

CREATE TABLE AbnormalBilling (
    invoice_id INT,
    reason VARCHAR(255),
    created_at TIMESTAMP
);

CREATE TABLE BillingAnalytics (
    customer_id INT,
    total_invoices INT,
    total_payments INT
);
