tables.table_schema:
CREATE TABLE ProviderInfo (
  provider_id INT PRIMARY KEY,
  provider_name VARCHAR(50),
  provider_address VARCHAR(100),
  provider_contact VARCHAR(50)
);

CREATE TABLE IndustryStandards (
  standard_id INT PRIMARY KEY,
  standard_name VARCHAR(50),
  standard_description TEXT
);

CREATE TABLE provider_information (
  provider_id INT PRIMARY KEY,
  provider_name VARCHAR(255),
  provider_location VARCHAR(255),
  industry_standard_rating INT
);

CREATE TABLE fraudulent_activities (
  activity_id INT PRIMARY KEY,
  provider_id INT REFERENCES provider_information(provider_id),
  activity_description TEXT,
  activity_timestamp TIMESTAMP
);

CREATE TABLE industry_standards (
  standard_id INT PRIMARY KEY,
  industry_name VARCHAR(255),
  standard_score INT
);

CREATE TABLE comparison_results (
  comparison_id INT PRIMARY KEY,
  provider_id INT REFERENCES provider_information(provider_id),
  standard_id INT REFERENCES industry_standards(standard_id),
  deviation_score INT
);

CREATE TABLE ProviderInfo (
    provider_id UUID PRIMARY KEY,
    provider_name STRING,
    provider_location STRING,
    provider_type STRING
);

CREATE TABLE IndustryStandards (
    standard_id UUID PRIMARY KEY,
    standard_name STRING,
    standard_category STRING,
    standard_description STRING
);

CREATE TABLE FraudulentActivities (
    activity_id UUID PRIMARY KEY,
    activity_type STRING,
    activity_description STRING,
    activity_date TIMESTAMP
);

CREATE TABLE DetectedFraud (
    fraud_id UUID PRIMARY KEY,
    fraud_description STRING,
    detected_date TIMESTAMP
);
