tables.table_schema:
CREATE TABLE flag_suspicious_transactions (
    transaction_id INT PRIMARY KEY,
    transaction_amount DECIMAL,
    transaction_date DATE,
    suspicious_reason TEXT
);

CREATE TABLE suspicious_transactions (
  transaction_id INT PRIMARY KEY,
  transaction_amount DECIMAL,
  transaction_date TIMESTAMP,
  customer_id INT
);

CREATE TABLE flagged_transactions (
  flagged_id INT PRIMARY KEY,
  transaction_id INT,
  flag_reason TEXT,
  flag_date TIMESTAMP
);

CREATE TABLE flagged_customers (
  customer_id INT PRIMARY KEY,
  flag_count INT,
  last_flag_date TIMESTAMP
);

CREATE TABLE flagged_accounts (
  account_id INT PRIMARY KEY,
  customer_id INT,
  flag_type TEXT,
  flag_status TEXT
);

CREATE TABLE flagged_locations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR,
  flag_count INT
);
