tables.table_schema:
CREATE TABLE medical_records (
  patient_id INT,
  patient_name VARCHAR(255),
  date_of_birth DATE,
  diagnosis VARCHAR(255)
);

CREATE TABLE access_logs (
  log_id INT,
  access_time TIMESTAMP,
  user_id INT,
  action VARCHAR(255)
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username STRING,
    password STRING,
    email STRING
);

CREATE TABLE patient_records (
    record_id INT PRIMARY KEY,
    patient_name STRING,
    diagnosis TEXT,
    treatment_plan TEXT
);

CREATE TABLE access_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    action_type STRING,
    timestamp TIMESTAMP
);

CREATE TABLE breach_reports (
    report_id INT PRIMARY KEY,
    patient_id INT,
    breach_details TEXT,
    report_date DATE
);
