tables.table_schema:
CREATE TABLE referrals (
  referral_id INT PRIMARY KEY,
  patient_id INT,
  doctor_id INT,
  referral_date DATE
);

CREATE TABLE kickback_schemes (
  scheme_id INT PRIMARY KEY,
  scheme_name VARCHAR(255),
  provider_id INT,
  kickback_amount DECIMAL(12, 2)
);

CREATE TABLE physicians (
    physician_id INT PRIMARY KEY,
    physician_name VARCHAR(255),
    specialty VARCHAR(100),
    location VARCHAR(100)
);
