tables.table_schema:
CREATE TABLE billing_processes(
    treatment_code INT,
    insurance_information STRING
);

CREATE TABLE billing_info (
  id SERIAL PRIMARY KEY,
  treatment_code VARCHAR(10) NOT NULL,
  insurance_provider VARCHAR(50) NOT NULL,
  amount_charged DECIMAL(10, 2) NOT NULL
);

CREATE TABLE claim_processing (
  id SERIAL PRIMARY KEY,
  claim_number VARCHAR(20) NOT NULL,
  claim_status VARCHAR(20) NOT NULL,
  claim_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE treatment_codes (
    code_id INT PRIMARY KEY,
    treatment_code VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE insurance_info (
    insurance_id INT PRIMARY KEY,
    insurance_name VARCHAR(100) NOT NULL,
    policy_number VARCHAR(50) UNIQUE,
    provider_name VARCHAR(100)
);
